import { USER_REGISTER_FAIL } from "../constants/actionType";


const initialState = {
      loading: false
};

const userReducer = (state = initialState, { type, payload })=> {
      switch (type) {
            case USER_REGISTER_FAIL:
                  return {
                        ...state,
                        loading:true
                  }
            
            default:
                  return state;
            
      }
}

export default userReducer;
