import { USER_LOGIN, USER_LOGIN_FAIL, USER_REGISTER, USER_REGISTER_FAIL, USER_REGISTER_SUCCESS } from "../constants/actionType";


const initialState = {
      loading: false,
      user:null
};

const userReducer = (state =initialState, { type, payload })=> {
      switch (type) {
            case USER_REGISTER,
                  USER_LOGIN:
                  return {
                        ...state,
                        loading: true
                  };
            
            case USER_REGISTER_SUCCESS:
                  return {
                        ...state,
                        loading: false,
                        user: payload
                  };
            
            case USER_REGISTER_FAIL,USER_LOGIN_FAIL:
                  return {
                        ...state,
                        loading: false,
                        errors: payload
                  };
            
            
            
            default:
                  return state;
            
      }
}

export default userReducer;
